mapper.select:
select

  distinct


from resource




  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

mapper.insert:
insert into resource (id, gmt_create, gmt_modified, 
  status_id, sort, recourse_name, 
  recourse_type, recourse_size, recourse_url, 
  video_status, video_length, video_vid
  )
values (#{id,jdbcType=BIGINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}, 
  #{statusId,jdbcType=TINYINT}, #{sort,jdbcType=INTEGER}, #{recourseName,jdbcType=VARCHAR}, 
  #{recourseType,jdbcType=TINYINT}, #{recourseSize,jdbcType=BIGINT}, #{recourseUrl,jdbcType=VARCHAR}, 
  #{videoStatus,jdbcType=TINYINT}, #{videoLength,jdbcType=VARCHAR}, #{videoVid,jdbcType=VARCHAR}
  )
