mapper.select:
select

  distinct


from resource




  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

mapper.insert:
insert into resource (id, gmt_create, gmt_modified, 
  status_id, sort, recource_name, 
  recource_type, recource_size, recource_url, 
  video_status, video_length, video_vid
  )
values (#{id,jdbcType=BIGINT}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModified,jdbcType=TIMESTAMP}, 
  #{statusId,jdbcType=TINYINT}, #{sort,jdbcType=INTEGER}, #{recourceName,jdbcType=VARCHAR}, 
  #{recourceType,jdbcType=TINYINT}, #{recourceSize,jdbcType=BIGINT}, #{recourceUrl,jdbcType=VARCHAR}, 
  #{videoStatus,jdbcType=TINYINT}, #{videoLength,jdbcType=VARCHAR}, #{videoVid,jdbcType=VARCHAR}
  )
